    private IList<ActionItem> todos = new List<ActionItem>();
    private string newTodo;

    private string doneClass = "";
    private string ActionKey = "";
    private string ActionType = "todo";

    private JobHuntRepository Actions;

    private bool hideDone = true;

    private List<DDOption> Options;
    private List<DDOption> Statuses;


    private IEnumerable<ActionItem> data;

    SideBar SideBar1 = new SideBar();

    Confirm confirm;

    private void ItemHandler(int index)
    {
        var todo = todos[index];

    }

    private void CheckedClicked(int index, bool b)
    {
        var t = todos[index];
        if (t != null && t.Id != null)
        {
            t.IsDone = b;
            _context.SaveChanges();
        }
    }

    private void CaptionChanged(int index, string text)
    {
        var t = todos[index];
        if (t != null && t.Id != null)
        {
            t.Title = text;
            _context.SaveChanges();
        }
    }

    private void handleNewTodoKeypress(KeyboardEventArgs args)
    {
        if (args.Key == "Enter")
        {
            AddTodo();
        }
    }


    private void AddTodo()
    {
        if (!string.IsNullOrWhiteSpace(newTodo))
        {
            _context.Add(new ActionItem { Title = newTodo, Type = ActionType });
            _context.SaveChanges();
            todos = getToDoList();
            newTodo = string.Empty;
        }
    }

    public void HandleSidebarOption(string key)
    {
        ActionKey = key;
        switch (key.ToLower())
        {
            case "toggledone":
                hideDone = !hideDone;
                break;
            case "deletedone":
                confirm.Show();
                break;
        }
        StateHasChanged();
    }

    public void HandleSetActionType(string key)
    {
        ActionType = key.ToLower();
        todos = getToDoList();
        newTodo = string.Empty;
        StateHasChanged();
    }


    public void HandleSetStatusFilter(string key)
    {
        string status = key.ToLower();
        todos = getToDoList();
        newTodo = string.Empty;
        StateHasChanged();
    }


    public void HandleDialogSelection(string key)
    {
        switch (key)
        {
            case "continue":
                ConfirmDelete(key);
                confirm.Hide();
                break;
            case "cancel":
                confirm.Hide();
                break;
        }
    }



    public void ConfirmDelete(string key)
    {
        todos.Where(t => t.IsDone).ToList().ForEach(t => _context.Remove(t));
        _context.SaveChanges();
        todos = getToDoList();
        StateHasChanged();
        confirm.Hide();
    }

    protected override void OnInitialized()
    {
        Actions = new JobHuntRepository(_context);
        todos = getToDoList();

        Options = new List<DDOption>();
        var opts = Actions.Types;
        foreach (var o in opts)
        {
            Options.Add(new DDOption(o.Id, o.Name));
        }

        Statuses = new List<DDOption>();
        Statuses.Add(new DDOption("*",       ""));
        Statuses.Add(new DDOption("",        "To Do"));
        Statuses.Add(new DDOption("started", "Started"));
        Statuses.Add(new DDOption("waiting", "Waiting"));
        Statuses.Add(new DDOption("hold",    "On Hold"));
        Statuses.Add(new DDOption("done",    "Done"));
    }

    List<ActionItem> getToDoList()
    {
        return Actions.AllActions.Where(a => a.Type == ActionType).ToList();
    }

    protected override async Task OnAfterRenderAsync(bool firstRender)
    {
        await SetTitle("Actions");
    }

    async Task SetTitle(string title)
    {
        await JsRuntime.InvokeVoidAsync(identifier: "setTitle",
            title);
    }

