    private void HandleButtonClick(string value)
    {
        switch (value.ToLower())
        {
            case "cancel":
                Hide();
                break;

            case "save":
                if (NoteId < 0)
                {
                    AddNote();
                }
                else
                {
                    UpdateNote();
                }
                OnChangeCallback.InvokeAsync("Done");
                break;
        }
    }


