        {
            Title = $"{ActionItem.Title} â€” Add New Note";

            NoteText = "";
        }
        else
        {
            Title = $"{ActionItem.Title} â€” Edit Note";
        }
        hidden = "";
        StateHasChanged();
    }

    private string AddNote()
    {
        Note note = new Note
        {
            User = "Tom",
            Type = "test",
            Text = NoteText
        };

        string result = Repository.AddNote(ActionItem.ActionItemId ?? 0, note);
        Hide();

        return result;
    }

